import styled from 'styled-components';

export default styled.input`
  width: 100%;
  background: #fff;
  box-shadow: 0px 4px 10px rgba(0, 0, 0, 0.04);
  height: 52px;
  border: 2px solid #fff;
  border: none;
  border-radius: 4px;
  outline: none;
  padding: 0 16px;
  font-size: 16px;

  &:focus {
    border-color: 2px solid ${({ theme }) => theme.colors.primary.main}
  }
`;
